mapper.select:
select comment_id as commentId, board_id as boardId, fu.user_nickname as userId, content, write_date as writeDate
from forj_comment as fc join forj_user as fu
using(user_id)
where board_id = #{boardId}

select comment_id as commentId, board_id as boardId, fu.user_nickname as userId, content, write_date as writeDate
from forj_comment as fc join forj_user as fu
using(user_id)
where comment_id = #{commentId}

mapper.update:
update forj_comment
set content=#{content}
where comment_id=#{commentId}
